import { createSlice } from "@reduxjs/toolkit";
import { getData } from "./thunkFunction";

const name=createSlice(
    {
        name:'names',
    initialState:{
        loading:true,
        data:null,
        error:false}  
        ,extraReducers:{
            [getData.fulfilled]:(state,action)=>{
             state.loading=true
             state.error=false
             state.data=null
            },
            [getData.rejected]:(state,action)=>{
             state.loading=false
             state.error=true
             state.data=null
            },
            [getData.loading]:(state,action)=>{
             state.loading=false
             state.error=false
             state.data=action.payload
            }
        }  
    }

)
let dataReducer=name.reducer
export default dataReducer
